package thujavavorkurs.abschnitt1_variableneingabeausgabe;

public class aufgabe3WasFuerEinTypBistDu {

    public static void main(String[] args) {
        // TODO Auto-generated method stub


        //Alter eines Menschen:
        int alter = 27;
        //Das Jahresgehalt in ganzen Euro-Beträgen:
        int jahresGehalt = 42000;
        //Das Geschlecht einer Person:
        String gender = "Weiblich";
        //Die Anrede einer Person:
        String anrede = "Frau";
        //Die eulersche Zahl e mit 14 Nachkommastellen:
        double e = 2.71828182845904;
        //Das Gewicht in kg:
        float gewicht = 65.8f;

        System.out.println("Alter: " + alter);
        System.out.println("Jahresgehalt: " + jahresGehalt);
        System.out.println("Geschlecht: " + gender);
        System.out.println("Anrede: " + anrede);
        System.out.println("Eulersche Zahl e: " + e);
        System.out.println("Gewicht " + gewicht);

    }

}
